import { useEffect, useContext, useState } from 'react';
import Navbar from './components/Navbar.js';
import Messages from './pages/Messages/index.js';
import Spinner from './components/elements/Spinner.js';
import axios from './lib/axios.js';
import { UserContext } from './context/UserContext.js';
import Footer from './components/Footer.js';

function App() {
  const [loading, setLoading] = useState(true);

  const { setAuth } = useContext(UserContext);

  useEffect(() => {
    const getUserInfo = async (token) => {
      try {
        const { data } = await axios.get('/api/users', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        // set auth context to true
        setAuth((prev) => ({
          ...prev,
          isAuth: true,
          name: data?.user.name,
          email: data?.user.email,
          is_admin: data?.user.is_admin,
          is_member: data?.user.is_member,
          id: data?.user._id,
        }));
      } catch (error) {
        console.error(error);
      }
    };

    const token = localStorage.getItem('token');

    if (token) {
      getUserInfo(token).finally(() => setLoading(false));
    } else {
      setLoading(false);
    }
  }, []);

  return loading ? (
    <div className="flex justify-center items-center absolute inset-0 m-auto">
      <Spinner />
    </div>
  ) : (
    <div>
      <Navbar />
      <Messages />
      <Footer />
    </div>
  );
}

export default App;
